mapper.sql:
SELECT
COUNT(1)
FROM
t_order_wholesale u
WHERE 1 = 1

    AND position (#{orders.customerName} in u.customer_name) > 0


    AND position (#{orders.customerPhone} in u.customer_phone) > 0


    AND u.goods_id = #{orders.goodsId}


    AND position (#{orders.goodsName} in u.goods_name) > 0


    And u.create_time > #{orders.createTimeFrom}


    And u.create_time < #{orders.createTimeTo}


    AND u.status = #{orders.status}


    AND u.orders_address = #{orders.ordersaddress}


    AND u.storehouse = #{orders.storehouse}

SELECT u.orders_id ordersId, u.customer_name customerName, u.customer_phone customerPhone, u.status status, u.create_time createTime, u.goods_id goodsId, u.goods_name goodsName, u.goods_num goodsNum, u.orders_address ordersaddress, u.price_paid pricepaid, u.orders_price ordersprice, u.orders_profile ordersprofile, u.storehouse storehouse, u.orders_period ordersperiod FROM t_order_wholesale u WHERE 1 = 1
AND position (#{orders.customerName} in u.customer_name) > 0
AND position (#{orders.customerPhone} in u.customer_phone) > 0
AND u.goods_id = #{orders.goodsId}
AND position (#{orders.goodsName} in u.goods_name) > 0
And u.create_time > #{orders.createTimeFrom}
And u.create_time < #{orders.createTimeTo}
AND u.status = #{orders.status}
AND u.orders_address = #{orders.ordersaddress}
AND u.storehouse = #{orders.storehouse}
-- ORDER BY ordersId DESC

SELECT
COUNT(1)
FROM
t_order_wholesale u
WHERE 1 = 1

    AND position (#{orders.customerName} in u.customer_name) > 0


    AND position (#{orders.customerPhone} in u.customer_phone) > 0


    AND u.goods_id = #{orders.goodsId}


    AND position (#{orders.goodsName} in u.goods_name) > 0


    And u.create_time > #{orders.createTimeFrom}


    And u.create_time < #{orders.createTimeTo}


    AND u.status = #{orders.status}


    AND u.status != 4


    AND u.orders_address = #{orders.ordersaddress}


    AND u.storehouse = #{orders.storehouse}

SELECT u.orders_id ordersId, u.customer_name customerName, u.customer_phone customerPhone, u.status status, u.create_time createTime, u.goods_id goodsId, u.goods_name goodsName, u.goods_num goodsNum, u.orders_address ordersaddress, u.price_paid pricepaid, u.orders_price ordersprice, u.orders_profile ordersprofile, u.storehouse storehouse, u.orders_period ordersperiod, u.orders_price-u.price_paid AS rest FROM t_order_wholesale u WHERE 1 = 1
AND position (#{orders.customerName} in u.customer_name) > 0
AND position (#{orders.customerPhone} in u.customer_phone) > 0
AND u.goods_id = #{orders.goodsId}
AND position (#{orders.goodsName} in u.goods_name) > 0
And u.create_time > #{orders.createTimeFrom}
And u.create_time < #{orders.createTimeTo}
AND u.status = #{orders.status}
AND u.status != 4
AND u.orders_address = #{orders.ordersaddress}
AND u.storehouse = #{orders.storehouse}
-- ORDER BY ordersId DESC
